
/**
 * Produce the sum of two numbers.
 *
 * @param {number} a 
 * @param {number} b 
 * @return {number} sum
 * @api public
 */

function sum (a, b) {
  return a+b
}

/**
 * Multiply two numbers.
 *
 * @param {number} a 
 * @param {number} b 
 * @return {number} res
 * @api private
 */

function mul (a, b) {
  return a*b
}
